usdPreviewSurfaceCombiner(
        float3 diffuseIrradianceIn,
        float3 specularIrradianceIn,
        float3 ambientIn,
        float3 irradianceEnv,
        float3 specularEnv,
        float3 diffuseColor,
        float3 specularColor,
        float3 emissiveColor,
        float metallic,
        bool useSpecularWorkflow,
        float ior,
        float occlusion,
        float EdotH,
        float3 transparency)
{
